var fs=require("fs"),glob=require("globby");module.exports=function(e,n){e=glob.sync(e).filter(function(e){return fs.lstatSync(e).isFile()}).map(function(e){return fs.readFileSync(e).toString()});fs.writeFileSync(n,e.join(""))};